#!/usr/bin/env ruby

def complement_dna_strand(dna_string)
  reversed_string = dna_string.reverse.chars
  complemented_string = []
  reversed_string.each do |n|
    case n
    when "T"
      complemented_string.push("A")
    when "A"
      complemented_string.push("T")
    when "C"
      complemented_string.push("G")
    when "G"
      complemented_string.push("C")
    end
  end
  puts complemented_string.join("")
end

complement_dna_strand("")
